       end if
    end if
    do ii=1,css%cobs
       css%obs_var(ii)=var(ii)
       call chop0(css%obs_var(ii),80)
       css%obs_lenv(ii)=length(css%obs_var(ii),80,10)
       css%obs_req(ii)=.false.
