  subroutine wo(ounit,ind,buff250)
    integer :: ounit
    integer :: ind
    character*250 :: buff250
    integer :: lenb
    integer, external :: length
    character*20 :: blank20="                    "
    call chop0(buff250,250)
    lenb=length(buff250,250,10)
    write(ounit,'(A)')blank20(1:max(0,min(20,ind)))//buff250(1:lenb)
    return
  end subroutine wo
