  FUNCTION parse_evalf (css, Val, crc250,irc) RESULT (res)
    !----- -------- --------- --------- --------- --------- --------- --------- -------
    ! Evaluate bytecode of ith function for the values passed in array Val(:)
    !----- -------- --------- --------- --------- --------- --------- --------- -------
    IMPLICIT NONE
    type(parse_session), pointer,INTENT(IN) :: css
    REAL(rn), allocatable, INTENT(in)       :: Val(:)  ! Variable values
    character*250 :: crc250
    integer :: irc
    REAL(rn)                                :: res     ! Result
    !
    INTEGER                                 :: IP,   & ! Instruction pointer
         DP,   & ! Data pointer
         SP,   & ! Stack pointer
         AP,   & ! arguments pointer
         AI      ! arguments index
    REAL(rn),                PARAMETER :: zero = 0._rn
    integer :: ii, imax,nargs
    character*22 :: myname ="parse_evalf"
    character*250 :: str250
    real(rn)  :: eps
    integer :: lens
    integer, external :: length
    logical :: above,below,found
    !----- -------- --------- --------- --------- --------- --------- --------- -------
    if (.not.associated(css)) then
       if(parse_bdeb)write(*,*)myname,"Input ",&
            & imax,allocated(val)
       irc=911
       call parse_errorappend(crc250,myname//" Invalid session.")
       return
    end if
    imax=size(val)
    css%clen=0
    if(parse_bdeb)write(*,*)myname,"Entering '"//css%funcStr100(1:css%lenf)//"'",imax,allocated(val)
    DP = 1
    SP = 0
    AP = 0 ! argument position
    AI = 0 ! argument function index
    DO IP=1,css%ByteCodeSize
       NARGS=css%ArgsByte(AI+1)
       if(parse_bdeb)then
          write(*,'(X,A,X,A,X,I3,X,I3,3X,A,3X,I0,X,I0)')myname,"Looping",&
               & IP,css%ByteCode(IP),parse_code20(css%bytecode(ip),nargs)
       end if
       SELECT CASE (css%ByteCode(IP))
       CASE (cImmed)
          SP=SP+1
          css%Stack(SP)=css%Immed(DP)
          DP=DP+1
       CASE   (cNeg)
          css%Stack(SP)=-css%Stack(SP)
       CASE   (cAdd)
          css%Stack(SP-1)=css%Stack(SP-1)+css%Stack(SP)
          SP=SP-1
       CASE   (cSub)
          css%Stack(SP-1)=css%Stack(SP-1)-css%Stack(SP)
          SP=SP-1
       CASE   (cMul)
          css%Stack(SP-1)=css%Stack(SP-1)*css%Stack(SP)
          SP=SP-1
       CASE   (cDiv)
          IF (css%Stack(SP)==0._rn) THEN
             if (parse_bdeb) write(*,*)"*** Division by zero."
             irc=313
             call parse_errorappend(crc250,myname//" Division by zero.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=1
             res=zero
             RETURN
          ENDIF
          css%Stack(SP-1)=css%Stack(SP-1)/css%Stack(SP)
          SP=SP-1
       CASE   (cPow)
          css%Stack(SP-1)=css%Stack(SP-1)**css%Stack(SP)
          SP=SP-1
       CASE   (cAbs)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=ABS(css%Stack(SP))
       CASE   (cExp)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=EXP(css%Stack(SP))
       CASE (cLog10)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (css%Stack(SP)<=0._rn) THEN
             if (parse_bdeb) write(*,*)"*** Negative argument to LOG10.",css%Stack(SP)
             irc=313
             call parse_errorappend(crc250,myname//" Negative argument to LOG10.")
             call parse_errorappendr(crc250,css%Stack(SP))
             call parse_errorappend(crc250,"\n")
             EvalErrType=3
             res=zero
             RETURN
          ENDIF
          css%Stack(SP)=LOG10(css%Stack(SP))
       CASE   (cLog)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (css%Stack(SP)<=0._rn) THEN
             if (parse_bdeb) write(*,*)"*** Negative argument to LOG.",css%Stack(SP)
             irc=313
             call parse_errorappend(crc250,myname//" Negative argument to LOG.")
             call parse_errorappendr(crc250,css%Stack(SP))
             call parse_errorappend(crc250,"\n")
             EvalErrType=3
             res=zero
             RETURN
          ENDIF
          css%Stack(SP)=LOG(css%Stack(SP))
       CASE  (cSqrt)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (css%Stack(SP)<0._rn) THEN
             if (parse_bdeb) write(*,*)"*** Negative argument to SQRT.",css%Stack(SP)
             irc=313
             call parse_errorappend(crc250,myname//" Negative argument to SQRT.")
             call parse_errorappendr(crc250,css%Stack(SP))
             call parse_errorappend(crc250,"\n")
             EvalErrType=2
             res=zero
             RETURN
          ENDIF
          css%Stack(SP)=SQRT(css%Stack(SP))
       CASE  (cMsgMax)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=1.0D0
       CASE  (cMsgMin)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=1.0D0
       CASE  (cMsgClosest)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=1.0D0
       CASE  (cisMember)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          found=.false.
          ISMEMBER: DO II=1,NARGS-1
             if (css%stack(SP).eq.css%Stack(SP+II)) then
                found=.true.
                exit ISMEMBER
             end if
          end do ISMEMBER
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cisBelow)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          found=.true.
          ISBELOW: DO II=1,NARGS-1
             if (css%stack(SP).ge.css%Stack(SP+II)) then
                found=.false.
                exit ISBELOW
             end if
          end do ISBELOW
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cisAbove)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          found=.true.
          ISABOVE: DO II=1,NARGS-1
             if (css%stack(SP).le.css%Stack(SP+II)) then
                found=.false.
                exit ISABOVE
             end if
          end do ISABOVE
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cisBetween)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          above=.false.
          below=.false.
          ISBETWEEN: DO II=1,NARGS-1
             if (css%stack(SP).le.css%Stack(SP+II)) then
                BELOW=.TRUE.
             end if
             if (css%stack(SP).ge.css%Stack(SP+II)) then
                ABOVE=.TRUE.
             end if
             if (above.and.below) exit ISBETWEEN
          end do ISBETWEEN
          if (above.and.below) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cThinned)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (NARGS.ge.1) THEN ! 
             if (rand()*100 .gt. css%Stack(SP)) then
                css%Stack(SP)=1.0D0
             else
                css%Stack(SP)=0.0D0
             end if
          end if
       CASE  (cand)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          found=(abs(css%stack(SP)).gt.0.5D0)
          if (found) then
             AND: DO II=1,NARGS-1
                if (abs(css%Stack(SP+II)).lt.0.5D0) then
                   found=.false.
                   exit AND
                end if
             end do AND
          end if
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cor)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          found=(abs(css%stack(SP)).gt.0.5D0)
          if (.not.found) then
             OR: DO II=1,NARGS-1
                if (abs(css%Stack(SP+II)).gt.0.5D0) then
                   found=.true.
                   exit OR
                end if
             end do OR
          end if
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cnot)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          ! .not.a .and. .not.b ....
          found=(abs(css%stack(SP)).lt.0.5D0)
          if (found) then
             NOT: DO II=1,NARGS-1
                if (abs(css%Stack(SP+II)).gt.0.5D0) then
                   found=.false.
                   exit NOT
                end if
             end do NOT
          end if
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (c1970yy)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_f1970_yy(css%Stack(SP))
       CASE  (c1970mm)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_f1970_mm(css%Stack(SP))
       CASE  (c1970dd)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_f1970_dd(css%Stack(SP))
       CASE  (c1970hh)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_f1970_hh(css%Stack(SP))
       CASE  (c1970mi)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_f1970_mi(css%Stack(SP))
       CASE  (c1970)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (NARGS.EQ.1) THEN ! dtg()
             css%Stack(SP)=css%Stack(SP)+parse_f1970(real(val8(1)),real(val8(2)),&
                  &real(val8(3)),real(val8(5)),real(val8(6)),real(val8(7)))
          ELSE IF (NARGS.EQ.6) THEN ! dtg(year,month,day,hour,min,sec)
             css%Stack(SP)=parse_f1970(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  css%Stack(SP+4), &
                  css%Stack(SP+5))
          ELSE IF (NARGS.EQ.5) THEN ! dtg(year,month,day,hour,min)
             css%Stack(SP)=parse_f1970(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  css%Stack(SP+4), &
                  0.0D0)
          ELSE IF (NARGS.EQ.4) THEN ! dtg(year,month,day,hour)
             css%Stack(SP)=parse_f1970(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  0.0D0, &
                  0.0D0)
          ELSE 
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to s1970:",nargs
             irc=308
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to s1970.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          END IF
       CASE  (cjulianyy)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_fjulian_yy(css%Stack(SP))
       CASE  (cjulianmm)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_fjulian_mm(css%Stack(SP))
       CASE  (cjuliandd)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_fjulian_dd(css%Stack(SP))
       CASE  (cjulianhh)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_fjulian_hh(css%Stack(SP))
       CASE  (cjulianmi)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_fjulian_mi(css%Stack(SP))
       CASE  (cjulian)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (NARGS.EQ.1) THEN ! dtg()
             css%Stack(SP)=css%Stack(SP)+parse_fjulian(real(val8(1)),real(val8(2)),&
                  &real(val8(3)),real(val8(5)),real(val8(6)),real(val8(7)))
          ELSE IF (NARGS.EQ.6) THEN ! dtg(year,month,day,hour,min,sec)
             css%Stack(SP)=parse_fjulian(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  css%Stack(SP+4), &
                  css%Stack(SP+5))
          ELSE IF (NARGS.EQ.5) THEN ! dtg(year,month,day,hour,min)
             css%Stack(SP)=parse_fjulian(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  css%Stack(SP+4), &
                  0.0D0)
          ELSE IF (NARGS.EQ.4) THEN ! dtg(year,month,day,hour)
             css%Stack(SP)=parse_fjulian(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  0.0D0, &
                  0.0D0)
          ELSE 
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to d2000:",nargs
             irc=309
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to d2000.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          END IF
       CASE  (cmidnight)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=css%Stack(SP)*secperday+parse_f1970(real(val8(1)),real(val8(2)),&
               &real(val8(3)),0.0D0,0.0D0,0.0D0)
          do ii=1,nargs-1
             css%Stack(SP)=css%Stack(SP)+css%Stack(SP+ii)*secperday             
          end do
       CASE  (cnow)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=css%Stack(SP)*secperday+parse_f1970(real(val8(1)),real(val8(2)),&
               &real(val8(3)),real(val8(5)),real(val8(6)),real(val8(7)))
          do ii=1,nargs-1
             css%Stack(SP)=css%Stack(SP)+css%Stack(SP+ii)*secperday
          end do
       CASE  (cround)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.gt.1) then
             eps=max(1.0D-5,abs(css%Stack(SP+1)))
             css%Stack(SP)=dnint(css%Stack(SP)/eps)*eps
          else
             css%Stack(SP)=dnint(css%Stack(SP))
          end if
       CASE  (cVarName)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.1) then
             if (parse_bdeb) write(*,*)"*** Found shape:",nint(css%stack(sp))
             if (allocated(css%cbuff)) deallocate(css%cbuff)
             css%clen=25
             allocate(character(len=css%clen) :: css%cbuff)
             css%cbuff=trim(getname25(css%Stack(SP)))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to shapes:",nargs
             irc=312
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to shapes.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (cValidRange)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.3) then
             if (parse_bdeb) write(*,*)"*** Found range-check"
             if (css%Stack(SP).lt.css%Stack(SP+1).or.&
                  & css%Stack(SP).gt.css%Stack(SP+2)) then
                if (allocated(css%cbuff)) deallocate(css%cbuff)
                css%clen=2
                allocate(character(len=css%clen) :: css%cbuff)
                css%cbuff="NA"
             end if
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to validRange:",nargs
             irc=312
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to validRange.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (cShpPre)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.2) then
             css%Stack(SP)=shapeid(css%Stack(SP),css%Stack(SP+1))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to shape:",nargs
             irc=310
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to shape.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (cShpVic)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.3) then
             css%Stack(SP)=vicinity(css%Stack(SP),css%Stack(SP+1),css%Stack(SP+2))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to shape:",nargs
             irc=310
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to shape.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (ctd2q) ! td,p
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.2) then
             css%Stack(SP)=td2q(css%Stack(SP),css%Stack(SP+1))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to td2q:",nargs
             irc=313
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to td2q.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (crh2td) ! rh, t, [ice=1,0]
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.2) then
             css%Stack(SP)=rh2td(css%Stack(SP),css%Stack(SP+1))
          else if (nargs.eq.3) then
             css%Stack(SP)=rh2td(css%Stack(SP),css%Stack(SP+1),css%Stack(SP+2))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to rh2td:",nargs
             irc=313
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to rh2td.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (ctd2rh) ! td, t, [ice=1,0]
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.2) then
             css%Stack(SP)=td2rh(css%Stack(SP),css%Stack(SP+1))
          else if (nargs.eq.3) then
             css%Stack(SP)=td2rh(css%Stack(SP),css%Stack(SP+1),css%Stack(SP+2))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to td2rh:",nargs
             irc=313
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to td2rh.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (cq2rh) ! q, t, [p|1013.25]
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.2) then
             css%Stack(SP)=q2rh(css%Stack(SP),css%Stack(SP+1))
          else if (nargs.eq.3) then
             css%Stack(SP)=q2rh(css%Stack(SP),css%Stack(SP+1),css%Stack(SP+2))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to q2rh:",nargs
             irc=313
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to q2rh.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (ck2c)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=css%Stack(SP)-t0
       CASE  (cc2k)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=css%Stack(SP)+t0
       CASE  (cSinh)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=SINH(css%Stack(SP))
       CASE  (cCosh)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=COSH(css%Stack(SP))
       CASE  (cTanh)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=TANH(css%Stack(SP))
       CASE   (cSin)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=SIN(css%Stack(SP))
       CASE   (cCos)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=COS(css%Stack(SP))
       CASE   (cTan)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=TAN(css%Stack(SP))
       CASE  (cAsin)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF ((css%Stack(SP)<-1._rn).OR.(css%Stack(SP)>1._rn)) THEN
             if (parse_bdeb) write(*,*)"*** Invalid argument to ASIN:",css%Stack(SP)
             irc=313
             call parse_errorappend(crc250,myname//" Invalid argument to ASIN.")
             call parse_errorappendr(crc250,css%Stack(SP))
             call parse_errorappend(crc250,"\n")
             EvalErrType=4
             res=zero
             RETURN
          ENDIF
          css%Stack(SP)=ASIN(css%Stack(SP))
       CASE  (cAcos)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF ((css%Stack(SP)<-1._rn).OR.(css%Stack(SP)>1._rn)) THEN
             if (parse_bdeb) write(*,*)"*** Invalid argument to ACOS:",css%Stack(SP)
             irc=313
             call parse_errorappend(crc250,myname//" Invalid argument to ACOS.")
             call parse_errorappendr(crc250,css%Stack(SP))
             call parse_errorappend(crc250,"\n")
             EvalErrType=4
             res=zero
             RETURN
          ENDIF
          css%Stack(SP)=ACOS(css%Stack(SP))
       CASE  (cAtan2)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (NARGS.EQ.2) THEN
             css%Stack(SP)=ATAN2(css%Stack(SP),css%Stack(SP+1))
          ELSE 
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to atan2:",nargs
             irc=310
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to atan2.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          END IF
       CASE  (cAtan)
          css%Stack(SP)=ATAN(css%Stack(SP))
       CASE  DEFAULT
          SP=SP+1
          if (css%ByteCode(IP) .le. css%VarEnd) then
             ii=css%ByteCode(IP)-VarBegin+1
             if (parse_bdeb.and.ii.gt.imax)write(*,*) myname,'Invalid VAL-index:',ii,"(max=",size(val),")"
             css%Stack(SP)=Val(ii)
          else
             css%Stack(SP)=ConstVal(css%ByteCode(IP)-css%VarEnd)
          end if
       END SELECT
       if(parse_bdeb)then
          if (sp.ne.0) then
             write(str250,'(100(X,F0.2))') &
                  & (css%Stack(II),II=1,min(SP,100))
          else
             STR250=""
          end if
          call chop0(str250,250)
          lens=length(str250,250,10)
          write(*,'(X,A,5X,A)') myname,&
               & "stack=["//str250(1:LENS)//"]"
       end if
    END DO
    EvalErrType = 0
    res = css%Stack(1)
    if(parse_bdeb)write(*,*) myname,"Result=",res
    !if(parse_bdeb)write(*,*)myname,"Done."
  END FUNCTION parse_evalf
  !
  subroutine parse_evals (css, Val, set, res, ret, crc250,irc) 
    !----- -------- --------- --------- --------- --------- --------- --------- -------
    ! Evaluate bytecode of ith function for the values passed in array Val(:)
    !----- -------- --------- --------- --------- --------- --------- --------- -------
    IMPLICIT NONE
    type(parse_session), pointer :: css
    REAL(rn), allocatable, INTENT(in)  :: Val(:)             ! Variable values
    logical, allocatable, INTENT(in)   :: set(:)            ! is variable set?
    REAL(rn)                           :: res                ! Result
    logical                            :: ret                ! is result set?
    character*250 :: crc250
    integer :: irc
    INTEGER                            :: IP,              & ! Instruction pointer
         DP,              & ! Data pointer
         SP,              & ! Stack pointer
         AP,              & ! arguments pointer
         AI                 ! arguments index
    REAL(rn),                PARAMETER :: zero = 0._rn
    integer :: ii, nargs
    character*22 :: myname ="parse_evals"
    character*250 :: str250
    real(rn) :: eps
    integer :: lens,imax
    integer, external :: length
    logical :: above,below,found
    !----- -------- --------- --------- --------- --------- --------- --------- -------
    if (.not.associated(css)) then
       if(parse_bdeb)write(*,*)myname,"Input ",&
            & imax,size(set),&
            & allocated(val),allocated(set)
       irc=911
       call parse_errorappend(crc250,myname//" Invalid session.")
       return
    end if
    imax=size(val)
    css%clen=0
    if(parse_bdeb)write(*,*)myname,"Entering '"//css%funcStr100(1:css%lenf)//"'",&
         & imax,size(set),&
         & allocated(val),allocated(set)
    if(parse_bdeb)write(*,*)myname,"Entering"
    ret=.true.
    DP = 1
    SP = 0
    AP = 0 ! argument position
    AI = 0 ! argument function index
    DO IP=1,css%ByteCodeSize
       SELECT CASE (css%ByteCode(IP))
       CASE (cImmed)
          SP=SP+1
          css%Stack(SP)=css%Immed(DP)
          DP=DP+1
       CASE   (cNeg)
          css%Stack(SP)=-css%Stack(SP)
       CASE   (cAdd)
          css%Stack(SP-1)=css%Stack(SP-1)+css%Stack(SP)
          SP=SP-1
       CASE   (cSub)
          css%Stack(SP-1)=css%Stack(SP-1)-css%Stack(SP)
          SP=SP-1
       CASE   (cMul)
          css%Stack(SP-1)=css%Stack(SP-1)*css%Stack(SP)
          SP=SP-1
       CASE   (cDiv)
          IF (css%Stack(SP)==0._rn) THEN
             if (parse_bdeb) write(*,*)"*** Division by zero."
             irc=313
             call parse_errorappend(crc250,myname//" Division by zero.\n")
             EvalErrType=1
             res=zero
             RETURN
          ENDIF
          css%Stack(SP-1)=css%Stack(SP-1)/css%Stack(SP)
          SP=SP-1
       CASE   (cPow)
          css%Stack(SP-1)=css%Stack(SP-1)**css%Stack(SP)
          SP=SP-1
       CASE   (cAbs)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=ABS(css%Stack(SP))
       CASE   (cExp)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=EXP(css%Stack(SP))
       CASE (cLog10)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (css%Stack(SP)<=0._rn) THEN
             if (parse_bdeb) write(*,*)"*** Negative argument to LOG10:",css%Stack(SP)
             irc=313
             call parse_errorappend(crc250,myname//" Negative argument to LOG10.")
             call parse_errorappendr(crc250,css%Stack(SP))
             call parse_errorappend(crc250,"\n")
             EvalErrType=3
             res=zero
             RETURN
          ENDIF
          css%Stack(SP)=LOG10(css%Stack(SP))
       CASE   (cLog)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (css%Stack(SP)<=0._rn) THEN
             if (parse_bdeb) write(*,*)"*** Negative argument to LOG:",css%Stack(SP)
             irc=313
             call parse_errorappend(crc250,myname//" Negative argument to LOG.")
             call parse_errorappendr(crc250,css%Stack(SP))
             call parse_errorappend(crc250,"\n")
             EvalErrType=3
             res=zero
             RETURN
          ENDIF
          css%Stack(SP)=LOG(css%Stack(SP))
       CASE  (cSqrt)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (css%Stack(SP)<0._rn) THEN
             if (parse_bdeb) write(*,*)"*** Negative argument to SQRT:",css%Stack(SP)
             irc=313
             call parse_errorappend(crc250,myname//" Negative argument to SQRT.")
             call parse_errorappendr(crc250,css%Stack(SP))
             call parse_errorappend(crc250,"\n")
             EvalErrType=3
             res=zero
             RETURN
          ENDIF
          css%Stack(SP)=SQRT(css%Stack(SP))
       CASE  (cMsgMax)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=1.0D0
       CASE  (cMsgMin)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=1.0D0
       CASE  (cMsgClosest)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=1.0D0
       CASE  (cisMember)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          found=.false.
          ISMEMBER: DO II=1,NARGS-1
             if (css%stack(SP).eq.css%Stack(SP+II)) then
                found=.true.
                exit ISMEMBER
             end if
          end do ISMEMBER
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cisBelow)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          found=.true.
          ISBELOW: DO II=1,NARGS-1
             if (css%stack(SP).ge.css%Stack(SP+II)) then
                found=.false.
                exit ISBELOW
             end if
          end do ISBELOW
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cisAbove)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          found=.true.
          ISABOVE: DO II=1,NARGS-1
             if (css%stack(SP).le.css%Stack(SP+II)) then
                found=.false.
                exit ISABOVE
             end if
          end do ISABOVE
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cisBetween)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          above=.false.
          below=.false.
          ISBETWEEN: DO II=1,NARGS-1
             if (css%stack(SP).le.css%Stack(SP+II)) then
                BELOW=.TRUE.
             end if
             if (css%stack(SP).ge.css%Stack(SP+II)) then
                ABOVE=.TRUE.
             end if
             if (above.and.below) exit ISBETWEEN
          end do ISBETWEEN
          if (above.and.below) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cThinned)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (NARGS.ge.1) THEN ! 
             if (rand()*100 .gt. css%Stack(SP)) then
                css%Stack(SP)=1.0D0
             else
                css%Stack(SP)=0.0D0
             end if
          end if
       CASE  (cand)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          found=(abs(css%stack(SP)).gt.0.5D0)
          if (found) then
             AND: DO II=1,NARGS-1
                if (abs(css%Stack(SP+II)).lt.0.5D0) then
                   found=.false.
                   exit AND
                end if
             end do AND
          end if
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cor)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          found=(abs(css%stack(SP)).gt.0.5D0)
          if (.not.found) then
             OR: DO II=1,NARGS-1
                if (abs(css%Stack(SP+II)).gt.0.5D0) then
                   found=.true.
                   exit OR
                end if
             end do OR
          end if
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (cnot)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          ! .not.a .and. .not.b ....
          found=(abs(css%stack(SP)).lt.0.5D0)
          if (found) then
             NOT: DO II=1,NARGS-1
                if (abs(css%Stack(SP+II)).gt.0.5D0) then
                   found=.false.
                   exit NOT
                end if
             end do NOT
          end if
          if (found) then
             css%Stack(SP)=1.0D0
          else
             css%Stack(SP)=0.0D0
          end if
       CASE  (c1970yy)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_f1970_yy(css%Stack(SP))
       CASE  (c1970mm)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_f1970_mm(css%Stack(SP))
       CASE  (c1970dd)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_f1970_dd(css%Stack(SP))
       CASE  (c1970hh)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_f1970_hh(css%Stack(SP))
       CASE  (c1970mi)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_f1970_mi(css%Stack(SP))
       CASE  (c1970)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (NARGS.EQ.1) THEN ! dtg()
             css%Stack(SP)=css%Stack(SP)+parse_f1970(real(val8(1)),real(val8(2)),&
                  &real(val8(3)),real(val8(5)),real(val8(6)),real(val8(7)))
          ELSE IF (NARGS.EQ.6) THEN ! dtg(year,month,day,hour,min,sec)
             css%Stack(SP)=parse_f1970(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  css%Stack(SP+4), &
                  css%Stack(SP+5))
          ELSE IF (NARGS.EQ.5) THEN ! dtg(year,month,day,hour,min)
             css%Stack(SP)=parse_f1970(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  css%Stack(SP+4), &
                  0.0D0)
          ELSE IF (NARGS.EQ.4) THEN ! dtg(year,month,day,hour)
             css%Stack(SP)=parse_f1970(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  0.0D0, &
                  0.0D0)
          ELSE 
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to s1970:",nargs
             irc=311
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to s1970.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          END IF
       CASE  (cjulianyy)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_fjulian_yy(css%Stack(SP))
       CASE  (cjulianmm)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_fjulian_mm(css%Stack(SP))
       CASE  (cjuliandd)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_fjulian_dd(css%Stack(SP))
       CASE  (cjulianhh)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_fjulian_hh(css%Stack(SP))
       CASE  (cjulianmi)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=parse_fjulian_mi(css%Stack(SP))
       CASE  (cjulian)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (NARGS.EQ.1) THEN ! dtg()
             css%Stack(SP)=css%Stack(SP)+parse_fjulian(real(val8(1)),real(val8(2)),&
                  &real(val8(3)),real(val8(5)),real(val8(6)),real(val8(7)))
          ELSE IF (NARGS.EQ.6) THEN ! dtg(year,month,day,hour,min,sec)
             css%Stack(SP)=parse_fjulian(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  css%Stack(SP+4), &
                  css%Stack(SP+5))
          ELSE IF (NARGS.EQ.5) THEN ! dtg(year,month,day,hour,min)
             css%Stack(SP)=parse_fjulian(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  css%Stack(SP+4), &
                  0.0D0)
          ELSE IF (NARGS.EQ.4) THEN ! dtg(year,month,day,hour)
             css%Stack(SP)=parse_fjulian(css%Stack(SP), &
                  css%Stack(SP+1), &
                  css%Stack(SP+2), &
                  css%Stack(SP+3), &
                  0.0D0, &
                  0.0D0)
          ELSE 
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to d2000:",nargs
             irc=312
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to d2000.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          END IF
       CASE  (cmidnight)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=css%Stack(SP)*secperday+parse_f1970(real(val8(1)),real(val8(2)),&
               &real(val8(3)),0.0D0,0.0D0,0.0D0)
          do ii=1,nargs-1
             css%Stack(SP)=css%Stack(SP)+css%Stack(SP+ii)*secperday
          end do
       CASE  (cnow)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=css%Stack(SP)*secperday+parse_f1970(real(val8(1)),real(val8(2)),&
               &real(val8(3)),real(val8(5)),real(val8(6)),real(val8(7)))
          do ii=1,nargs-1
             css%Stack(SP)=css%Stack(SP)+css%Stack(SP+ii)*secperday
          end do
       CASE  (cround)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.gt.1) then
             eps=max(1.0D-5,abs(css%Stack(SP+1)))
             css%Stack(SP)=dnint(css%Stack(SP)/eps)*eps
          else
             css%Stack(SP)=dnint(css%Stack(SP))
          end if
       CASE  (cVarName)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.1) then
             if (parse_bdeb) write(*,*)"*** Found shape:",nint(css%stack(sp))
             if (allocated(css%cbuff)) deallocate(css%cbuff)
             css%clen=25
             allocate(character(len=css%clen) :: css%cbuff)
             css%cbuff=trim(getname25(css%Stack(SP)))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to shapes:",nargs
             irc=312
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to shapes.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (cValidRange)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.3) then
             if (parse_bdeb) write(*,*)"*** Found range-check"
             if (css%Stack(SP).lt.css%Stack(SP+1).or.&
                  & css%Stack(SP).gt.css%Stack(SP+2)) then
                if (allocated(css%cbuff)) deallocate(css%cbuff)
                css%clen=2
                allocate(character(len=css%clen) :: css%cbuff)
                css%cbuff="NA"
             end if
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to rangeCheck:",nargs
             irc=312
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to rangeCheck.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (cShpPre)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.2) then
             css%Stack(SP)=shapeid(css%Stack(SP),css%Stack(SP+1))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to shape:",nargs
             irc=310
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to shape.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (cShpVic)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.3) then
             css%Stack(SP)=vicinity(css%Stack(SP),css%Stack(SP+1),css%Stack(SP+2))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to shape:",nargs
             irc=310
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to shape.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (ctd2q) ! td,p
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.2) then
             css%Stack(SP)=td2q(css%Stack(SP),css%Stack(SP+1))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to td2q:",nargs
             irc=313
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to td2q.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (crh2td) ! rh, t, [ice=1,0]
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.2) then
             css%Stack(SP)=rh2td(css%Stack(SP),css%Stack(SP+1))
          else if (nargs.eq.3) then
             css%Stack(SP)=rh2td(css%Stack(SP),css%Stack(SP+1),css%Stack(SP+2))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to rh2td:",nargs
             irc=313
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to rh2td.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (ctd2rh) ! td, t, [ice=1,0]
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.2) then
             css%Stack(SP)=td2rh(css%Stack(SP),css%Stack(SP+1))
          else if (nargs.eq.3) then
             css%Stack(SP)=td2rh(css%Stack(SP),css%Stack(SP+1),css%Stack(SP+2))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to td2rh:",nargs
             irc=313
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to td2rh.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (cq2rh) ! q, t, [p|1013.25]
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          if (nargs.eq.2) then
             css%Stack(SP)=q2rh(css%Stack(SP),css%Stack(SP+1))
          else if (nargs.eq.3) then
             css%Stack(SP)=q2rh(css%Stack(SP),css%Stack(SP+1),css%Stack(SP+2))
          else
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to q2rh:",nargs
             irc=313
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to q2rh.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          end if
       CASE  (ck2c)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=css%Stack(SP)-t0
       CASE  (cc2k)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=css%Stack(SP)+t0
       CASE  (cSinh)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=SINH(css%Stack(SP))
       CASE  (cCosh)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=COSH(css%Stack(SP))
       CASE  (cTanh)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=TANH(css%Stack(SP))
       CASE   (cSin)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=SIN(css%Stack(SP))
       CASE   (cCos)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=COS(css%Stack(SP))
       CASE   (cTan)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          css%Stack(SP)=TAN(css%Stack(SP))
       CASE  (cAsin)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF ((css%Stack(SP)<-1._rn).OR.(css%Stack(SP)>1._rn)) THEN
             if (parse_bdeb) write(*,*)"*** Invalid argument to ASIN:",css%Stack(SP)
             irc=313
             call parse_errorappend(crc250,myname//" Invalid argument to ASIN.")
             call parse_errorappendr(crc250,css%Stack(SP))
             call parse_errorappend(crc250,"\n")
             EvalErrType=4
             res=zero
             RETURN
          ENDIF
          css%Stack(SP)=ASIN(css%Stack(SP))
       CASE  (cAcos)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF ((css%Stack(SP)<-1._rn).OR.(css%Stack(SP)>1._rn)) THEN
             if (parse_bdeb) write(*,*)"*** Invalid argument to ACOS:",css%Stack(SP)
             irc=313
             call parse_errorappend(crc250,myname//" Invalid argument to ACOS.")
             call parse_errorappendr(crc250,css%Stack(SP))
             call parse_errorappend(crc250,"\n")
             EvalErrType=4
             res=zero
             RETURN
          ENDIF
          css%Stack(SP)=ACOS(css%Stack(SP))
       CASE  (cAtan2)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IF (NARGS.EQ.2) THEN
             css%Stack(SP)=ATAN2(css%Stack(SP),css%Stack(SP+1))
          ELSE 
             if (parse_bdeb) write(*,*)"*** Unexpected number of arguments to atan2:",nargs
             irc=313
             call parse_errorappend(crc250,myname//" Unexpected number of arguments to atan2.")
             call parse_errorappendi(crc250,nargs)
             call parse_errorappend(crc250,"\n")
             EvalErrType=5
             res=zero
             RETURN
          END IF
       CASE  (cAtan)
          css%Stack(SP)=ATAN(css%Stack(SP))
       CASE  DEFAULT
          SP=SP+1
          if (css%ByteCode(IP) .le. css%VarEnd) then
             css%Stack(SP)=Val(css%ByteCode(IP)-VarBegin+1)
             if (.not.set(css%ByteCode(IP)-VarBegin+1)) ret=.false.
          else
             css%Stack(SP)=ConstVal(css%ByteCode(IP)-css%VarEnd)
          end if
       END SELECT
       if(parse_bdeb)then
          if (sp.ne.0) then
             write(str250,'(100(X,F0.2))') &
                  & (css%Stack(II),II=1,min(SP,100))
          else
             STR250=""
          end if
          call chop0(str250,250)
          lens=length(str250,250,10)
          write(*,'(X,A,5X,A)') myname,&
               & "Stack=["//str250(1:LENS)//"]"
       end if
    END DO
    EvalErrType = 0
    res = css%Stack(1)
    if(parse_bdeb)write(*,'(X,A,X,A,X,F0.2,X,L1)') myname," result=",res,ret
    if(parse_bdeb)write(*,*)myname,"Done."
  END subroutine parse_evals
  !
  subroutine parse_evala (css, ctrg, cpos, npos, Val, Set, Res, crc250,irc)
    !----- -------- --------- --------- --------- --------- --------- --------- -------
    ! Evaluate bytecode of ith function for the values passed in array Val(:)
    !----- -------- --------- --------- --------- --------- --------- --------- -------
    IMPLICIT NONE
    type(parse_session), pointer,INTENT(IN):: css
    integer(is),INTENT(in)                 :: ctrg,cpos  ! allocated targets, positions
    integer(is),INTENT(in)                 :: npos       ! number of positions
    REAL(rn), allocatable, INTENT(in)      :: Val(:,:)   ! Variable values
    logical, allocatable, INTENT(in)       :: Set(:)     ! is position defined?
    REAL(rn),allocatable,INTENT(INOUT)       :: res(:)     ! Result
    character*250 :: crc250
    integer :: irc
    INTEGER                            :: IP,              & ! Instruction pointer
         DP,              & ! Data pointer
         SP,              & ! Stack pointer
         AP,              & ! arguments pointer
         AI                 ! arguments index
    REAL(rn),                PARAMETER :: zero = 0._rn
    integer :: ii, jj, nargs, imax,imin,iclo
    real(rn) :: vmax,vmin,vclo,v,buff
    logical :: above, below, found
    character*250 :: str250
    real(rn) :: eps
    integer :: lens
    integer, external :: length
    character*12 :: myname ="parse_evala"
    !----- -------- --------- --------- --------- --------- --------- --------- -------
    if (.not.associated(css)) then
       if(parse_bdeb)write(*,*)myname,"Input ",&
            & ctrg, cpos, npos, size(val),size(set),size(res),&
            & allocated(val),allocated(set),allocated(res)
       irc=911
       call parse_errorappend(crc250,myname//" Invalid session.")
       return
    end if
    css%clen=0
    if(parse_bdeb)write(*,*)myname,"Entering '"//css%funcStr100(1:css%lenf)//"'",&
         & ctrg, cpos, npos, size(val),size(set),size(res),&
         & allocated(val),allocated(set),allocated(res)
    if (css%npos.lt.npos) then
       css%npos=npos
       IF (ASSOCIATED(css%Stacka)) DEALLOCATE ( css%Stacka, stat=irc)
       IF (ASSOCIATED(css%Wrka))   DEALLOCATE ( css%Wrka,   stat=irc)
       ALLOCATE (css%Stacka(css%StackSize,css%npos),css%Wrka(css%npos),STAT = irc)
       IF (irc.ne. 0) THEN
          call parse_errorappend(crc250,myname//" Unable to allocate stacka, stacka, wrka.\n")
          return
       end if
    end if
    !
    DP = 1
    SP = 0
    AP = 0
    AI = 0
    DO IP=1,css%ByteCodeSize
       if(parse_bdeb)then
          NARGS=css%ArgsByte(AI+1)
          write(*,*)myname,'Index:',ip,css%ByteCodeSize
          write(*,'(X,A,X,A,X,I3,X,I3,3X,A,3X,I0,X,I0)')myname,"Looping",&
               & IP,css%ByteCode(IP),parse_code20(css%bytecode(ip),nargs)
       end if
       SELECT CASE (css%ByteCode(IP))
       CASE (cImmed)
          if(parse_bdeb)write(*,*)myname,'Immed enter:',SP,DP,NPOS,size(set)
          SP=SP+1
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                if(parse_bdeb)write(*,*)myname,'Immed eval:',SP,jj,&
                     & associated(css%stacka),associated(css%immed)
                css%Stacka(SP,JJ)=css%Immed(DP)
             END IF
          END DO
          DP=DP+1
          if(parse_bdeb)write(*,*)myname,'Immed done:',SP,DP,NPOS,size(set)
       CASE   (cNeg)
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                if (parse_bdeb)write(*,*)myname,'Neg.'
                css%Stacka(SP,JJ)=-css%Stacka(SP,JJ)
             END IF
          END DO
       CASE   (cAdd)
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                css%Stacka(SP-1,JJ)=css%Stacka(SP-1,JJ)+css%Stacka(SP,JJ)
             END IF
          END DO
          SP=SP-1
       CASE   (cSub)
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                css%Stacka(SP-1,JJ)=css%Stacka(SP-1,JJ)-css%Stacka(SP,JJ)
             END IF
          END DO
          SP=SP-1
       CASE   (cMul)
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                css%Stacka(SP-1,JJ)=css%Stacka(SP-1,JJ)*css%Stacka(SP,JJ)
             END IF
          END DO
          SP=SP-1
       CASE   (cDiv)
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                IF (css%Stacka(SP,JJ)==0._rn) THEN
                   if (parse_bdeb) write(*,*)"*** Division by zero."
                   irc=314
                   call parse_errorappend(crc250,myname//" Division by zero.\n")
                   EvalErrType=1
                   RETURN
                ENDIF
                css%Stacka(SP-1,JJ)=css%Stacka(SP-1,JJ)/css%Stacka(SP,JJ)
             END IF
          END DO
          SP=SP-1
       CASE   (cPow)
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                css%Stacka(SP-1,JJ)=css%Stacka(SP-1,JJ)**css%Stacka(SP,JJ)
             END IF
          END DO
          SP=SP-1
       CASE   (cAbs)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                css%Stacka(SP,JJ)=ABS(css%Stacka(SP,JJ))
             END IF
          END DO
       CASE   (cExp)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                css%Stacka(SP,JJ)=EXP(css%Stacka(SP,JJ))
             END IF
          END DO
       CASE (cLog10)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                IF (css%Stacka(SP,JJ)<=0._rn) THEN
                   if (parse_bdeb) write(*,*)"*** Invalid argument to ACOS:",css%Stack(SP)
                   irc=315
                   call parse_errorappend(crc250,myname//" Negative argument to LOG10.")
                   call parse_errorappendr(crc250,css%Stack(SP))
                   call parse_errorappend(crc250,"\n")
                   EvalErrType=3
                   RETURN
                ELSE
                   css%Stacka(SP,JJ)=LOG10(css%Stacka(SP,JJ))
                ENDIF
             END IF
          END DO
       CASE   (cLog)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                IF (css%Stacka(SP,JJ)<=0._rn) THEN
                   if (parse_bdeb) write(*,*)"*** Invalid argument to ACOS:",css%Stack(SP)
                   irc=316
                   call parse_errorappend(crc250,myname//" Negative argument to LOG.")
                   call parse_errorappendr(crc250,css%Stack(SP))
                   call parse_errorappend(crc250,"\n")
                   EvalErrType=3
                   RETURN
                ELSE
                   css%Stacka(SP,JJ)=LOG(css%Stacka(SP,JJ))
                ENDIF
             END IF
          END DO
       CASE  (cSqrt)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          DO JJ=1,NPOS
             IF(SET(JJ))THEN
                IF (css%Stacka(SP,JJ)<0._rn) THEN
                   if (parse_bdeb) write(*,*)"*** Invalid argument to ACOS:",css%Stack(SP)
                   irc=317
                   call parse_errorappend(crc250,myname//" Negative argument to SQRT.")
                   call parse_errorappendr(crc250,css%Stack(SP))
                   call parse_errorappend(crc250,"\n")
                   EvalErrType=3
                   RETURN
                ELSE
                   css%Stacka(SP,JJ)=SQRT(css%Stacka(SP,JJ))
                ENDIF
             END IF
          END DO
       CASE  (cMsgMax)
          AI=AI+1
          NARGS=css%ArgsByte(AI)
          SP=SP-NARGS+1
          IMAX=0
          VMAX=0.0D0
          DO JJ=1,NPOS
             if(set(jj))then
                if (imax.eq.0) then
                   imax=jj
