  subroutine plot_unlinkAttribute(cat)
    implicit none
    type(plot_attribute), pointer :: cat !  current attribute
    cat%prev%next => cat%next
    cat%next%prev => cat%prev
    return
  end subroutine plot_unlinkAttribute
