  subroutine plot_deallocateAttribute(cat)
    implicit none
    type(plot_attribute), pointer :: cat !  current attribute
    integer :: irc2
    deallocate(cat,stat=irc2) ! ignore any errors
    return
  end subroutine plot_deallocateAttribute
