  real function shape_ddpoly(POS,N,XPOS,ACTUAL)
    ! gives smalles distance to cartesian polygon
    implicit none
    integer n
    real pos(3),xpos(3,n)
    logical actual(n)
    INTEGER i,j
    real dd,ddmin
    logical llmin
    shape_ddpoly=-1.0D0
    llmin=.false.
    ddmin=-1.0D0
    if (n.le.1) return
    LOOP: DO I=1,N
       J=1+MOD(I,N)
       if (actual(i)) then
          dd=shape_getDist(pos,xpos(1,i),xpos(1,j))
          if (llmin) then
             ddmin=min(ddmin,dd)
          else
             ddmin=dd
             llmin=.true.
          end if
       end if
    end do LOOP
    shape_ddpoly=ddmin
    RETURN
  END function shape_ddpoly
